/* litan.h  view source at `beiyuer.github.io` or `hncsltok.github.io` */                                                                                                   typedef int bool; const bool true = 1; const bool false = 0; typedef char *string; void put_char(char x) { printf("%c", x); } void put_int(int x) { printf("%d", x); } void put_float(float x) { printf("%f", x); } void put_string(string x) { char *c; for (c=x; (*c)!='\0'; c++) { printf("%c", *c); } } void put_bool(bool x) { put_string(x ? "true" : "false"); } char get_char() { char x; scanf("%c", &x); return x; } int get_int() { int x; scanf("%d", &x); return x; } float get_float() { float x; scanf("%f", &x); return x; }
